def min_characters_to_modify(s):
    n = len(s)
    dp = [0] * n
    dp[0] = 0

    for i in range(1, n):
        if s[i] != s[i - 1]:
            dp[i] = dp[i - 1]
        else:
            dp[i] = dp[i - 1] + 1

    min_characters = 0
