  // Get user input
  const userInput = document.getElementById('user-input').value;

  // Preprocess and encode user input
  const userEncoded = transformers.tokenize(userInput);

  // Preprocess and encode BlenderBot-400m-distill's response
  const botEncoded = transformers.tokenize(blenderbot.generate(userEncoded));

  // Render chat messages
