#include <iostream>
using namespace std;

int longestIncreasingSubsequence(int arr[], int n) {
    int i, j;
    int longest = 0;
    for (i = 0; i < n - 1; i++) {
        for (j = i + 1; j < n; j++) {
            if (arr[j] > arr[i]) {
                longest = max(longest, j - i);
            }
        }
    }
    return longest;
}

int main() {
    int arr[] = {3, 1, 4, 1, 5, 9, 2, 6, 5, 3};
