#include <iostream>
#include <string>
#include <algorithm>
#include <cmath>

// Function to perform the Karatsuba algorithm for multiplication of two integers.
long long karatsuba(long long x, long long y) {
    // Base case for recursion
    if (x < 10 || y < 10) return x * y;

    // Calculate the size of the numbers
    int size = std::max(std::to_string(x).size(), std::to_string(y).size());
    int half_size = size / 2;

    // Split the digit sequences at the half point
    long long high1 = x / static_cast<long long>(std::pow(10, half_size));
    long long low1 = x % static_cast<long long>(std::pow(10, half_size));
    long long high2 = y / static_cast<long long>(std::pow(10, half_size));
    long long low2 = y % static_cast<long long>(std::pow(10, half_size));

    // Recursively calculate three products with smaller numbers
    long long z0 = karatsuba(low1, low2);
    long long z1 = karatsuba((low1 + high1), (low2 + high2));
    long long z2 = karatsuba(high1, high2);

    // Combine the results using the Karatsuba formula
    return (z2 * static_cast<long long>(std::pow(10, 2 * half_size))) +
           ((z1 - z2 - z0) * static_cast<long long>(std::pow(10, half_size))) +
           z0;
}

int main(int argc, char *argv[]) {
    // Check if the correct number of arguments have been provided
    if (argc != 3) {
        std::cerr << "Usage: " << argv[0] << " <number1> <number2>" << std::endl;
        return 1;
